import React, { useState } from 'react';
import ProductList from './ProductList';
import { ShoppingCartIcon } from '@heroicons/react/outline';

function App() {
  const [cart, setCart] = useState([]);
  const [coupon, setCoupon] = useState('');
  const [discount, setDiscount] = useState(0);
  const shippingFee = 100;

  const products = [
    { id: 1, name: 'เยอรมันเซเพิร์ด', price: 55000 , imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTn4ir7qxd2_RlqMp6l12zjeLI_EE3sO1ulMZYDrHrlN-x2SbHu' },
    { id: 2, name: 'ร็อตไวเลอร์', price: 86000, imageUrl: 'https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcRqxPFF_xFaR5WdQvlTBRAxhA5WYW4fJZXEOhTXOAMQHa9Cg6d_' },
    { id: 3, name: 'ชิวาวา', price: 40000, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQuw4EbRAydWv4TOy8rEU0qalFs9BS06bFiNaCqOYAiybZSkBpt' },
    { id: 4, name: 'ชิบะอินุ', price: 70000, imageUrl: 'https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcQypf5CQt8llGBVlsNtnbMuZWOJDOM5uPFM9RsPMlOYpFlEzRSs' },
    { id: 5, name: 'เพมโบรก เวลช์ คอร์กี้', price: 75000, imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR3FZBF76vbmr0mJbe5uKaIVGXYQRWYRHWh3vt1wnwTzMxXMHcG' },
    { id: 6, name: 'โดเบอร์แมน', price: 59000, imageUrl: 'https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcT4J7V4nAE2_Nwq-Bker5xF5Rs0vkA8z7lip7w09INQARmt77-3' },
    { id: 7, name: 'อเมริกันบูลลี่', price: 79000, imageUrl: 'data:image/webp;base64,' },
    { id: 8, name: 'บูลด็อก', price: 69000, imageUrl: 'data:image/webp;base64,' },
    { id: 9, name: 'ไซบีเรียน ฮัสกี', price: 95000, imageUrl: 'data:image/webp;base64,' },
    { id: 10, name: 'อเมริกัน เอสกิโม ด็อก', price: 245000, imageUrl: 'https://image.dogilike.com/shareimg/contentimg/2020/aomsin_/Ranking/7-24/shutterstock_1078824218-1024x683.jpg' },
  ];

  const couponCodes = {
    'DOGDOG35': 35,
    'DOGDOG50': 50,
    // เพิ่มคูปองเพิ่มเติมที่นี่
  };

  const addToCart = (product) => {
    const existingProduct = cart.find(item => item.id === product.id);

    if (existingProduct) {
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, amount) => {
    setCart(cart.map(item =>
      item.id === productId
        ? { ...item, quantity: Math.max(1, item.quantity + amount) }
        : item
    ));
  };

  const applyCoupon = () => {
    if (couponCodes[coupon]) {
      setDiscount(couponCodes[coupon]);
    } else {
      setDiscount(0);
      alert('คูปองไม่ถูกต้อง');
    }
    setCoupon('');
  };

  const calculateTotal = () => {
    const subtotal = cart.reduce((total, item) => total + (item.price * item.quantity), 0);
    const totalAfterDiscount = subtotal - (subtotal * discount / 100);
    return totalAfterDiscount + (cart.length > 0 ? shippingFee : 0);
  };

  const handleCheckout = () => {
    if (cart.length === 0) {
      alert('กรุณาเพิ่มสินค้าในตะกร้าก่อนทำการชำระเงิน'); // Alert if cart is empty
    } else {
      // Proceed to payment logic here
      alert(`Total amount to pay: ${calculateTotal()} บาท`); // Example action
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="container mx-auto flex flex-col items-center">
        <h1 className="text-3xl font-bold my-8 text-center">DOG DOG Shop</h1>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full max-w-6xl">
          {/* Sidebar: Shopping Cart */}
          <div className="col-span-1 bg-white p-4 shadow-lg rounded-md">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-2xl font-bold">ตะกร้าสินค้า</h2>
            </div>

            {cart.length > 0 && (
              <>
                <ul>
                  {cart.map((item) => (
                    <li key={item.id} className="border-b py-2">
                      <div className="flex justify-between items-center">
                        <span>{item.name} - {item.price} บาท x {item.quantity}</span>
                        <div className="flex items-center space-x-2">
                          <button onClick={() => updateQuantity(item.id, -1)} className="bg-red-500 text-white px-2 py-1 rounded-md">-</button>
                          <button onClick={() => updateQuantity(item.id, 1)} className="bg-green-500 text-white px-2 py-1 rounded-md">+</button>
                          <button onClick={() => removeFromCart(item.id)} className="bg-gray-500 text-white px-2 py-1 rounded-md">ลบ</button>
                        </div>
                      </div>
                    </li>
                  ))}
                </ul>

                {/* Coupon Section */}
                <div className="mt-4">
                  <input
                    type="text"
                    value={coupon}
                    onChange={(e) => setCoupon(e.target.value)}
                    placeholder="ใส่คูปอง"
                    className="border p-2 rounded-md w-full"
                  />
                  <button onClick={applyCoupon} className="bg-blue-500 text-white px-4 py-2 rounded-md mt-2">
                    ใช้คูปอง
                  </button>
                </div>

                {/* Shipping Fee and Total Price */}
                <div className="mt-4">
                  <h2 className="text-xl">ค่าขนส่ง: {shippingFee} บาท</h2>
                  <h2 className="text-xl font-bold mt-4">ราคารวม: {calculateTotal()} บาท</h2>
                  <button onClick={handleCheckout} className="bg-blue-500 text-white px-4 py-2 rounded-md mt-2">
                    ชำระเงิน
                  </button>
                </div>
              </>
            )}
            <h2>รายการสินค้า</h2>
          </div>

          {/* Product List */}
          <div className="col-span-2">
            <ProductList products={products} addToCart={addToCart} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;